import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, InputGroup, FormControl, Button, Row, Card } from 'react-bootstrap';
import { useState, useEffect } from 'react';

const CLIENT_ID = 'db9d36bee3254e8f8da1a5aa01789194';
const CLIENT_SECRET = 'd33d80a4bca14760b0c29b12e5bca469';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [accessToken, setAccessToken] = useState('');

  useEffect(() => {
    // API access token
    var authParameters = {
      method: 'POST',
      headers: { // Changed 'header' to 'headers'
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: 'grant_type=client_credentials&client_id=' + CLIENT_ID + '&client_secret=' + CLIENT_SECRET,
    };

    fetch('https://accounts.spotify.com/api/token', authParameters)
      .then(result => result.json())
      .catch(error => {
        console.log(error); // Log the error to the console
        // Display an error message to the user
      })
      .then(data => {
        console.log(data);
        setAccessToken(data.access_token);
      });
  }, []);

  return (
    <div className="App">
      <Container>
        <InputGroup className='mb-3' size='lg'>
          <FormControl
            placeholder='Search for an Artist' // Corrected "a Artist" to "an Artist"
            type='input'
            onKeyPress={(event) => {
              if (event.key === 'Enter') {
                console.log("pressed enter");
              }
            }}
            onChange={(event) => setSearchInput(event.target.value)} // Added a closing parenthesis
          />
          <Button onClick={(event) => console.log("clicked button")}>Search</Button> {/* Changed 'button' to 'Button' */}
        </InputGroup>
      </Container>
      <Container>
        <Row className='mb-2 row-cols-4'>
          <Card>
            <Card.Img src='#' />
            <Card.Body>
              <Card.Title>Album Name</Card.Title> {/* Removed extra space inside the title */}
            </Card.Body>
          </Card>
        </Row>
      </Container>
    </div>
  );
}

export default App;
