import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, InputGroup, FormControl, Button, Row, Card } from 'react-bootstrap';
import { useState, useEffect } from 'react';

const CLIENT_ID = 'db9d36bee3254e8f8da1a5aa01789194';
const CLIENT_SECRET = 'd33d80a4bca14760b0c29b12e5bca469';

function App() {
  const [searchInput, setSearchInput] = useState('');
  const [accessToken, setAccessToken] = useState('');

  useEffect(() => {
    // API access token
    var authParameters = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: 'grant_type=client_credentials&client_id=' + CLIENT_ID + '&client_secret=' + CLIENT_SECRET,
    };

    fetch('https://accounts.spotify.com/api/token', authParameters)
      .then(result => result.json())
      .then(data => {
        setAccessToken(data.access_token);
      });
  }, []);

  // Search function
  async function search() {
    console.log("Searching for", searchInput);

    var searchParameters = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + accessToken,
      },
    };

    // Get request to search for the artist and obtain the artist ID
    var artistID = await fetch('https://api.spotify.com/v1/search?q=' + searchInput + '&type=artist&limit=1', searchParameters)
      .then(response => response.json())
      .then(data => data.artists.items[0].id); // Fixed the syntax for extracting artist ID
    console.log("Artist ID:", artistID);

    // Add code here to further process the artist ID and fetch albums.

    var albums = await fetch('https://api.spotify.com/v1/artists/' + artistID + '/albums?include_groups=album&market=IN&limit=50', searchParameters) // Fixed the query string
      .then(response => response.json())
      .then(data => {
        console.log(data);
      });
  }

  return (
    <div className="App">
      <Container>
        <InputGroup className='mb-3' size='lg'>
          <FormControl
            placeholder='Search for an Artist'
            type='input'
            onKeyPress={(event) => {
              if (event.key === 'Enter') {
                search();
              }
            }}
            onChange={(event) => setSearchInput(event.target.value)}
          />
          <Button onClick={search}>Search</Button>
        </InputGroup>
      </Container>
      <Container>
        <Row className='mb-2 row-cols-4'>
          <Card>
            <Card.Img src='#' />
            <Card.Body>
              <Card.Title>Album Name</Card.Title>
            </Card.Body>
          </Card>
        </Row>
      </Container>
    </div>
  );
}

export default App;
