import {useState, useEffect} from 'react';

function App() {
  const [searchInput, setSearchInput] = useState('');

  return (
    <div className="App">
      <Container>
        <InputGroup className='mb-3' size='lg'>
          <FormControl
            placeholder='Search for a  Artist'
            type='input'
            onKeyPress={(event) => {
              if (event.key === 'Enter') {
                console.log("pressed enter");
              }
            }}
            onChange={(event => setSearchInput(event.target.value))} // Closing parenthesis added
          />
          <button onClick={(event) =>console.log("clicked button")}>
          Search
          </button>
        </InputGroup>
      </Container>
      <Container>
      <Card></Card>
      </Container>
    </div>
  );
}

export default App;
